    {
        parent::__construct();
    }
    
    /*
     * Get bank_detail by bank_id
     */
    function get_bank_detail($bank_id)
    {
        return $this->db->get_where('bank_details',array('bank_id'=>$bank_id))->row_array();
    }
        
    /*
     * Get all bank_details
     */
    function get_all_bank_details()
    {
        $this->db->order_by('bank_id', 'desc');
        return $this->db->get('bank_details')->result_array();
    }
        
