     */
    function add_bank_detail($params)
    {
        $this->db->insert('bank_details',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update bank_detail
     */
    function update_bank_detail($bank_id,$params)
    {
        $this->db->where('bank_id',$bank_id);
        return $this->db->update('bank_details',$params);
    }
    
    /*
     * function to delete bank_detail
     */
    function delete_bank_detail($bank_id)
    {
        return $this->db->delete('bank_details',array('bank_id'=>$bank_id));
    }
}
